import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Header } from './Header';
import { Calendar } from './Calendar';
import { FinancialEntryForm } from './FinancialEntryForm';
import { useFinancialData } from '../hooks/useFinancialData';

const Dashboard: React.FC = () => {
  const { user, logout } = useAuth();
  const {
    entries,
    recurringPayments,
    addEntry,
    updateEntry,
    deleteEntry,
    addRecurringPayment,
    updateRecurringPayment,
    deleteRecurringPayment,
  } = useFinancialData();

  return (
    <div className="dashboard">
      <Header user={user} onLogout={logout} />
      <div className="dashboard-content">
        <div className="dashboard-sidebar">
          <FinancialEntryForm onSubmit={addEntry} />
        </div>
        <div className="dashboard-main">
          <Calendar
            entries={entries}
            recurringPayments={recurringPayments}
            onUpdateEntry={updateEntry}
            onDeleteEntry={deleteEntry}
            onAddRecurringPayment={addRecurringPayment}
            onUpdateRecurringPayment={updateRecurringPayment}
            onDeleteRecurringPayment={deleteRecurringPayment}
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
